sourcesContent:
import HttpStatus from 'http-status-codes';
import * as NoteService from '../services/notes.service';

/**
 * Controller to create a new note
 * @param  {object} req - request object
 * @param {object} res - response object
 * @param {Function} next
 */

export const createNotes=async (req,res,next) =>{
    try{
        const data = await NoteService.createNotes(req.body);
        res.status(HttpStatus.CREATED).json({
            code: HttpStatus.CREATED,
            data: data,
            message:`Notes created successfully!!!.....`
        });
    }catch(error){
        next(error);
    }
};
export const getAllNotes = async(req,res,next)=>{
    try {
        const data = await NoteService.getAllNotes(req.body.userId);
        res.status(HttpStatus.OK).json({
            code:HttpStatus.OK,
            data:data,
            message:`All notes are get successfully!!!...`
        });
    } catch (error) {
        next(error);
    }
}

/**
 * Controller to get a single note
 */
export const getNote = async (req, res, next) => {
    try {
        const data = await NoteService.getNote(req.params._id, req.body.userId);
        if (data) {
            res.status(HttpStatus.OK).json({
                code: HttpStatus.OK,
                data: data,
                message: 'Note fetched successfully'
            });
        } else {
            res.status(HttpStatus.NOT_FOUND).json({
                code: HttpStatus.NOT_FOUND,
                message: 'Note not fouond'
            });
        } 
    } catch (error) {
        next(error);
    }
};

/**
 * Controller to update a note
 */
export const updateNotes = async (req, res, next) => {
    try {
        const data = await NoteService.updateNote(req.params._id, req.body);
        if (data){
            res.status(HttpStatus.ACCEPTED).json({
                code: HttpStatus.ACCEPTED,
                data: data,
                message: 'Note updated successfully'
            });
        } else {
            res.status(HttpStatus.NOT_FOUND).json({
                code: HttpStatus.NOT_FOUND,
                message: 'Note not found'
            });
        }
    } catch (error) {
        next(error);
    }
};

/**
 * Controller to delete a note
 */
export const deleteNote = async (req, res, next) => {
    try {
        await NoteService.deleteNote(req.params._id, req.body.userId);
        res.status(HttpStatus.OK).json({
            code: HttpStatus.OK,
            data: [],
            message: 'Note deleted successfully'
        });
    } catch (error) {
        next(error);
    }
};

