import { Schema, model } from 'mongoose';

const noteSchema = new Schema(
  {
    title: {
      type: String,
    },
    description: {
      type: String,
    },
    color: {
      type: String, 
    },
    isArchive: {
      type: Boolean,
      default: false
    },
    isTrash: {
      type: Boolean,
      default:false
    },
    userID:{
      type: String
    }
  },
  {
    timestamps: true,
    versionKey: false
  }
);

export default model('Note', noteSchema);

